import { useState } from 'react';
export default function App() {

  const [where, setWhere] = useState('');
  const [when, setWhen] = useState('');
  const [howMuch, setHowMuch] = useState(0);
  const [all, setAll] = useState([]);

  function handleSubmit(e) {
    e.preventDefault();
    if(!where || !when || !howMuch) return;

    setWhere(e.target.value);
    setWhen(e.target.value);
    setHowMuch(e.target.value);

    setAll(where,when,howMuch)
    console.log(all);
  }


  return (
    <div>
      <h1>Expense Tracer ... </h1>
      <form>
        <ul>
          <li>
            <label>Where do you was?</label>
            <input type='text' value={where} onChange={(e) => setWhere(e.target.value)}/>
          </li>
          <li>
            <label>When do you buy it?</label>
            <input type="date" value={when} onChange={(e)=> setWhen(e.target.value)}/>
          </li>
          <li>
            <label>How much do you spend?</label>
            <input type='number' value={howMuch} onChange={(e)=>setHowMuch(e.target.value)} />
          </li>
        </ul>
        <button type='submit'>Save</button>
      </form>
    </div>
  );
}
